pragma solidity >=0.6.0 <0.8.0;
import "./IBEP20.sol";

contract Betting {
    IBEP20 private token;
    address private owner;
    uint256 public betIndex;
    uint256 public totalBets;
    uint256 public totalRefunds;
    uint256 public totalPayout;

    constructor(IBEP20 _token) {
        token = _token;
        owner = msg.sender;
        betIndex = 1;
        totalBets = 0;
        totalRefunds = 0;
        totalPayout = 0;
    }

    struct BetStruct {
        address user;
        string opcode;
        uint256 value;
        string betTxId;
        string payoutTxId;
        string finalStatus;
    }

    modifier onlyOwner {
        //function caller must be contract owner
        require(msg.sender == owner, "only owner");
        _;
    }

    mapping(uint256 => BetStruct) public Bets;
    event Bet(
        uint256 betIndex,
        address indexed user,
        string opcode,
        uint256 value
    );
    event Refund(uint256 betIndex);
    event PayoutProcessed(uint256 betIndex);

    // Function to withdraw all Ether from this contract.
    function withdraw(uint256 _amount) public onlyOwner returns (bool success) {
        // get the amount of Ether stored in this contract
        uint256 amount = token.balanceOf(address(this));

        require(amount >= _amount, "amount exceeds");

        // send all token to owner
        token.transfer(owner, _amount);
        return true;
    }

    function doBet(string memory _opcode, uint256 _amount) public {
        //check amount cannot be less then 25
        require(_amount >= 25 ether, "minimum bet 25 BWGR");

        //opcode required
        require(bytes(_opcode).length > 6, "invalid opcode");

        //transfer bet amount
        token.transferFrom(msg.sender, address(this), _amount);

        //store bet with action=pending
        Bets[betIndex] = BetStruct(
            msg.sender,
            _opcode,
            _amount,
            "",
            "",
            "pending"
        );

        totalBets = totalBets + _amount;
        //emit bet event
        emit Bet(betIndex, msg.sender, _opcode, _amount);

        //increase bet (index/count)
        betIndex++;
    }

    function refund(uint256 _betIndex) public onlyOwner returns (bool success) {
        //require valid betIndex
        require(_betIndex < betIndex, "invalid betIndex");

        //check final status should be pending.
        require(
            keccak256(abi.encodePacked(Bets[_betIndex].finalStatus)) ==
                keccak256(abi.encodePacked("pending")),
            "bet already processed"
        );

        //get bet by index
        address user = Bets[_betIndex].user;
        uint256 amount = Bets[_betIndex].value;

        //refund full bet amount to user
        token.transfer(user, amount);

        //update bet status
        Bets[_betIndex].finalStatus = "refunded";
        totalRefunds = totalRefunds + amount;

        emit Refund(_betIndex);

        return true;
    }

    function updateBetTx(uint256 _betIndex, string memory _txId)
        public
        onlyOwner
        returns (bool success)
    {
        //require valid betIndex
        require(_betIndex < betIndex, "invalid betIndex");

        //check final status should be pending.
        require(
            keccak256(abi.encodePacked(Bets[_betIndex].finalStatus)) ==
                keccak256(abi.encodePacked("pending")),
            "bet already processed"
        );

        //require txid empty
        require(
            bytes(Bets[_betIndex].betTxId).length == 0,
            "betTxId already updated"
        );

        //require betTxId
        require(bytes(_txId).length > 0, "txId cannot be empty");

        Bets[_betIndex].betTxId = _txId;

        //update bet status
        Bets[_betIndex].finalStatus = "processed";

        return true;
    }

    function processPayout(
        uint256 _betIndex,
        uint256 _payout,
        string memory _payoutTx
    ) public onlyOwner returns (bool success) {
        //require valid betIndex
        require(_betIndex < betIndex, "invalid betIndex");

        //check final status should be pending.
        require(
            keccak256(abi.encodePacked(Bets[_betIndex].finalStatus)) ==
                keccak256(abi.encodePacked("processed")),
            "bet already processed"
        );

        //require payoutTxId
        require(bytes(_payoutTx).length > 0, "payoutTxId cannot be empty");

        //required payout amount
        require(_payout > 1 ether, "payout amount required");

        //get bet by index
        address user = Bets[_betIndex].user;

        //send payout
        token.transfer(user, _payout);

        //store WGR chain payouttx id
        Bets[_betIndex].payoutTxId = _payoutTx;

        totalPayout = totalPayout + _payout;

        //update bet status
        Bets[_betIndex].finalStatus = "completed";

        emit PayoutProcessed(_betIndex);

        return true;
    }
}
